import React, { useState, useEffect } from 'react';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import Animation from '../pages/Animation';
import Loader from '../pages/Loader';
import '../CSS/Header.css';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Trigger loader and navigation
  const handleNavigation = (e, path) => {
    e.preventDefault();
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false); // Stop loader after 2 seconds
      navigate(path); // Navigate to the desired path
    }, 2000);
  };

  return (
    <>
      <Loader isLoading={isLoading} />
      <header
        className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ease-in-out ${isScrolled
          ? 'bg-white shadow-lg py-4'
          : 'bg-black py-2'
        }`}
      >
        <Animation />
        <div className="container mx-auto px-4 flex justify-between items-center">
          <NavLink
            to="/"
            className={`header-logo relative text-4xl ${isScrolled ? 'text-red-500' : 'text-red-400'}`}
          >
            PRH <span className={`absolute text-xs w-max ms-1 ${isScrolled ? 'text-red-500 top-0' : 'text-red-400 top-3'}`}>Property Rental Hub</span>
          </NavLink>
          <ul className="header-nav flex items-center gap-10">
            <li>
              <a
                href="/properties"
                onClick={(e) => handleNavigation(e, '/properties')}
                className={`block py-2 pr-4 pl-3 lg:hover:bg-transparent lg:border-0 lg:p-0 ${isScrolled
                  ? 'text-black py-4'
                  : 'text-white py-2'
                }`}
              >
                Properties
              </a>
            </li>
            <li>
              <a
                href="/about"
                onClick={(e) => handleNavigation(e, '/about')}
                className={`text-white-700 block py-2 pr-4 pl-3 lg:hover:bg-transparent lg:border-0 lg:p-0 ${isScrolled
                  ? 'text-black py-4'
                  : 'text-white py-2'
                }`}
              >
                About
              </a>
            </li>
            <li>
              <a
                href="/contact"
                onClick={(e) => handleNavigation(e, '/contact')}
                className={`text-white-700 block py-2 pr-4 pl-3 lg:hover:bg-transparent lg:border-0 lg:p-0 ${isScrolled
                  ? 'text-black py-4'
                  : 'text-white py-2'
                }`}
              >
                Contact
              </a>
            </li>
          </ul>
          <div className="header-buttons flex gap-8">
            <Link
              to="/login"
              className="login-btn ps-3 pe-3 pt-1 pb-1 flex items-center text-white bg-blue-600 rounded hover:bg-blue-700"
            >
              Login
            </Link>
            <Link
              to="/signup"
              className="signup-btn px-3 py-1 flex items-center bg-red-500 text-white rounded hover:bg-red-600 transition"
            >
              Sign Up
            </Link>
          </div>
        </div>
      </header>
    </>
  );
};

export default Header;
