import React, { useEffect } from 'react';
import "../CSS/animation.css";

const Animation = () => {
    const handleScrollForMultipleElements = () => {
        const elementSelectors = [
            '.animate-on-scroll',
            '.animate-on-scroll-left',
            '.animate-on-scroll-right',
            '.animate-on-scroll-zoom'
        ];
        const elements = document.querySelectorAll(elementSelectors.join(','));

        elements.forEach(element => {
            if (isPartiallyInViewport(element)) {
                const animation = element.getAttribute('data-animation'); // Assuming you have a `data-animation` attribute if needed
                element.classList.add('animate');
            }
        });
    };

    const isPartiallyInViewport = (element) => {
        const rect = element.getBoundingClientRect();
        const windowHeight = window.innerHeight || document.documentElement.clientHeight;
        const windowWidth = window.innerWidth || document.documentElement.clientWidth;

        const vertInView = (rect.top <= windowHeight) && ((rect.top + rect.height) >= 0);
        const horInView = (rect.left <= windowWidth) && ((rect.left + rect.width) >= 0);

        return vertInView && horInView;
    };

    useEffect(() => {
        handleScrollForMultipleElements(); // Check elements in view on mount
        window.addEventListener('scroll', handleScrollForMultipleElements);

        // Clean up the event listener on component unmount
        return () => {
            window.removeEventListener('scroll', handleScrollForMultipleElements);
        };
    }, []);

    // This component doesn't return any JSX since it's only meant to trigger animations
    return null;
};

export default Animation;
